def ddexact(x, y):
    theta = get_theta(x, y)
    duxx = (5*sin((5*theta)/3))/(3*(x**2 + y**2)**(1/6)) - (5*x**2*sin((5*theta)/3))/(9*(x**2 + y**2)**(7/6)) - (10*y**3*cos((5*theta)/3)*(x**2 + y**2)**(5/6))/(3*x*(y**2 + x**2)**2) - (25*y**2*sin((5*theta)/3)*(x**2 + y**2)**(5/6))/(9*x**4*(y**2/x**2 + 1)**2) - (50*y*cos((5*theta)/3))/(9*x*(x**2 + y**2)**(1/6)*(y**2/x**2 + 1)) + (10*y*cos((5*theta)/3)*(x**2 + y**2)**(5/6))/(3*x**3*(y**2/x**2 + 1))
    duxy = (25*cos((5*theta)/3))/(9*(x**2 + y**2)**(1/6)*(y**2/x**2 + 1)) - (5*cos((5*theta)/3)*(x**2 + y**2)**(5/6))/(3*x**2*(y**2/x**2 + 1)) - (5*x*y*sin((5*theta)/3))/(9*(x**2 + y**2)**(7/6)) - (25*y**2*cos((5*theta)/3))/(9*x**2*(x**2 + y**2)**(1/6)*(y**2/x**2 + 1)) + (10*y**2*cos((5*theta)/3)*(x**2 + y**2)**(5/6))/(3*x**4*(y**2/x**2 + 1)**2) + (25*y*sin((5*theta)/3)*(x**2 + y**2)**(5/6))/(9*x**3*(y**2/x**2 + 1)**2)
    duyx = duxy
    duyy = (5*sin((5*theta)/3))/(3*(x**2 + y**2)**(1/6)) - (5*y**2*sin((5*theta)/3))/(9*(x**2 + y**2)**(7/6)) - (25*sin((5*theta)/3)*(x**2 + y**2)**(5/6))/(9*x**2*(y**2/x**2 + 1)**2) + (50*y*cos((5*theta)/3))/(9*x*(x**2 + y**2)**(1/6)*(y**2/x**2 + 1)) - (10*y*cos((5*theta)/3)*(x**2 + y**2)**(5/6))/(3*x**3*(y**2/x**2 + 1)**2)
    return duxx, duxy, duyx, duyy